import { Injectable } from "@angular/core";
import { CartProduct } from "./products";

@Injectable({
  providedIn: "root",
})
export class CartService {
  items: CartProduct[] = [];
  total: number = 0;

  constructor() {}

  addToCart(product: CartProduct) {
    this.items.push(product);
  }

  getItems() {
    return this.items;
  }

  clearCart() {
    this.items = [];
    return this.items;
  }

  setTotal(total:number) {
    this.total = total;
  }

  getTotal() {
    return this.total;
  }
}
